mapper.select:
SELECT forster_service.*, st_distance(POINT(lat, lon), POINT(#{lat}, #{lon}))*11195 AS distance FROM forster_service WHERE is_open = 1 AND state = 1
AND province LIKE #{province}
AND province LIKE #{city}
ORDER BY distance ASC LIMIT #{curIndex}, #{pageSize}

SELECT foster.*, st_distance(POINT(foster.lat, foster.lon), POINT(#{lat}, #{lon}))*11195 AS distance, user.name AS user_name, user.avatar_url AS user_avatar_url, user.age AS user_age, user.gender AS user_gender, user.phone AS user_phone, user.email AS user_email, user.regist_at AS user_regist_at FROM foster_service AS foster LEFT JOIN app_user AS user ON user.user_id = foster.user_id WHERE foster.is_open = 1 AND state = 1
AND foster.province LIKE #{province}
AND foster.city LIKE #{city}
ORDER BY distance ASC LIMIT #{curIndex}, #{pageSize}
